tc_game_scripts.script_contents:
IF EXIST __CoD2MP_s (
  attrib -h __CoD2MP_s
  del __CoD2MP_s
)

for /d %%f in (*) do (
  if %%f% neq Docs (
  if %%f% neq miles (
  if %%f% neq pb (

    IF EXIST %%f%\players\active.txt (
      del %%f%\players\active.txt
    )

    IF NOT EXIST %%f%\players (
      md %%f%\players
    )

    echo tcadmin > %%f%\players\active.txt

    IF EXIST %%f%\players\tcadmin (
      rd /q /s %%f%\players\tcadmin
    )

    md %%f%\players\tcadmin    

  )))
)
