  set userapppath=%HOMEDRIVE%\Users\TCAGame\AppData\Local
)

IF EXIST %userapppath%\Activision\CoDWaW\__CoDWaWmp (
  attrib -h %userapppath%\Activision\CoDWaW\__CoDWaWmp
  del %userapppath%\Activision\CoDWaW\__CoDWaWmp
