tc_game_config_files.template:
// Host Server Name
hostname "$[HostName]"

// Rcon Password
rcon_password "$[RConPassword]"

// Suit Power Drains
sv_infinite_aux_power "0"

// Maximum player lives per level
mp_lifecount "5"

// Minimum number of players to set spawn time
mp_spawncount "3"

// Spawn time
mp_spawntime "120"

// Ingame Vote System
votesys_allowvotes "1"
votesys_percent "65"

// Skill level
skill "2"

tc_game_scripts.script_contents:
import clr;
import System;

clr.AddReference("TCAdmin.SDK")

from System import String, Environment, PlatformID;
from System.IO import File, Path;
from System.Diagnostics import Process;

ThisTaskStep.WriteLog("Downloading Synergy files...")

with Process() as p:
  p.StartInfo.FileName=Path.Combine(TCAdminFolder, "Monitor", "Tools", "SteamCmd", "steamcmd.exe");
  p.StartInfo.Arguments=String.Format("+login \"{0}\" \"{1}\" \"NONE\" +force_install_dir \"{2}\" +app_update 17520 validate +quit", 
     ThisGame.Steam.SteamAcount, ThisGame.Steam.SteamPassword, Path.Combine(ThisServer.GameFilesPath, ThisGame.Paths.FilesFolderName, "Synergy"));
  p.StartInfo.WorkingDirectory=ThisService.WorkingDirectory;
  p.Start();
  p.WaitForExit();

ThisTaskStep.WriteLog("Downloading Half-Life 2 files...")

with Process() as p2:
  p2.StartInfo.FileName=Path.Combine(TCAdminFolder, "Monitor", "Tools", "SteamCmd", "steamcmd.exe");
  p2.StartInfo.Arguments=String.Format("+login \"{0}\" \"{1}\" \"NONE\" +force_install_dir \"{2}\" +app_update 220 validate +quit", 
     ThisGame.Steam.SteamAcount, ThisGame.Steam.SteamPassword, Path.Combine(ThisServer.GameFilesPath, ThisGame.Paths.FilesFolderName, "Half-Life 2"));
  p2.StartInfo.WorkingDirectory=ThisService.WorkingDirectory;
  p2.Start();
  p2.WaitForExit();

ThisService.Variables["SteamAccount"]=ThisGame.Steam.SteamAcount
ThisService.Save()

