@echo off echo.
echo Updating Synergy... "%TCAdminFolder%\Monitor\Tools\SteamCmd\steamcmd.exe" +login "%ThisService_SteamAccount%" +force_install_dir "%ThisService_RootDirectory%Synergy" +app_update "17520" +quit echo.
