tc_game_scripts.script_contents:
@echo off
SET dbname=%ThisService_UserName%_%ThisService_ServiceId% SET user=%ThisUser_UserName% SET password=%ThisUser_Password% SET hostinfo=%ThisService_IpHostname%
SET HOUR=%time:~0,2% SET dtStamp9=%date:~-4%%date:~4,2%%date:~7,2%_0%time:~1,1%%time:~3,2%%time:~6,2% SET dtStamp24=%date:~-4%%date:~4,2%%date:~7,2%_%time:~0,2%%time:~3,2%%time:~6,2%
if "%HOUR:~0,1%" == " " (SET dtStamp=%dtStamp9%) else (SET dtStamp=%dtStamp24%)
if not exist "%ThisService_RootDirectory%\DB_Backup" mkdir "%ThisService_RootDirectory%\DB_Backup" mysqldump -h localhost -uroot -pYOUR_MYSQL_PASS %dbname% -r "%ThisService_RootDirectory%\DB_Backup\DB_%dtStamp%.sql" del "%ThisService_RootDirectory%\DB_Backup\DB_latest.sql" mysqldump -h localhost -uroot -pYOUR_MYSQL_PASS %dbname% -r "%ThisService_RootDirectory%\DB_Backup\DB_latest.sql" echo Backup was stored in folder DB_Backup!
