tc_game_scripts.script_contents:
import clr;
import System;

clr.AddReference("TCAdmin.SDK")

from System import String, Environment, PlatformID;
from System.IO import File, Path;
from System.Diagnostics import Process;
from System.Threading import Thread;
from TCAdmin.SDK.Misc import Linux;

superusertxt = Path.Combine(ThisService_WorkingDirectory, "SuperUser.txt");
if File.Exists(superusertxt) :
  supw = File.ReadAllText(superusertxt);

  p = Process();
  p.StartInfo.FileName=ThisService_Executable;
  p.StartInfo.Arguments=String.Format("-ini murmur.ini -supw {0}", supw);
  p.StartInfo.WorkingDirectory=ThisService_WorkingDirectory;
  p.Start();

  if Environment.OSVersion.Platform == PlatformID.Win32NT :
    Thread.Sleep(3500);
    p.Kill();
  else:
    p.WaitForExit();
    if Linux.IsRoot() :
      Linux.SetDirectoryOwnerAutoDetect(ThisService_RootDirectory, True);
