import clr;
import System;

clr.AddReference("TCAdmin.SDK");
clr.AddReference("TCAdmin.GameHosting.SDK");

from System import Exception;
from TCAdmin.GameHosting.SDK.Objects import TeamSpeakServer;
from TCAdmin.SDK.Database import DatabaseManager;

db= DatabaseManager.CreateDatabaseManager()
result = db.Execute("select service_id from tc_teamspeak_servers where service_id=" + ThisService.ServiceId.ToString())
if result.Rows.Count == 1 :
  raise Exception("This Teamspeak master is already configured in System > Settings > Teamspeak Servers.");


ts = TeamSpeakServer();
ts.ServiceId = ThisService.ServiceId;
ts.DatacenterId = ThisServer.DatacenterId;
ts.Name = ThisService.IpHostname;
ts.Hostname = ThisService.IpAddress;
ts.TelnetPort = ThisService.RConPort;
ts.Version = 3;
ts.StartPort = 9987;
ts.SuperAdminLogin = "serveradmin";
ts.SuperAdminPassword = ThisService.Variables["RConPassword"];
ts.WelcomeMessage = "Welcome";
ts.Enabled = True;
ts.UploadQuota = 100;
ts.DownloadQuota = 100;
ts.EnableSnapShots = True;
ts.MaxSnapShots = 5;
ts.GenerateKey();
ts.Save()

import clr;
import System;

clr.AddReference("TCAdmin.SDK")

from System import String, Environment, PlatformID;
from System.Diagnostics import Process;
from System.Threading import Thread;
from System.IO import Path

#-- Set serveradmin password
if Environment.OSVersion.Platform == PlatformID.Win32NT :
 p = Process();
 p.StartInfo.FileName=ThisService.Executable;
 p.StartInfo.Arguments=String.Format("serveradmin_password={0}", ThisService.Variables["RConPassword"]);
 p.StartInfo.WorkingDirectory=ThisService.WorkingDirectory;
 p.Start();

 Thread.Sleep(3500);
 if not p.HasExited :
  p.Kill();

else:
 runas=ThisService.GetServiceCredentials().User;
 p = Process();
 p.StartInfo.FileName="chown";
 p.StartInfo.Arguments=String.Format("--recursive {0}:{0} {1}", runas, ThisService.WorkingDirectory);
 p.Start();
 p.WaitForExit();

 p = Process();
 p.StartInfo.FileName="su";
 p.StartInfo.Arguments=String.Format("{0} -c\"./ts3server_minimal_runscript.sh license_accepted=1 create_default_virtualserver=0 logappend=1 no_password_dialog=1 inifile=ts3server.ini serveradmin_password={1}\"", runas, ThisService.Variables["RConPassword"]);
 p.StartInfo.WorkingDirectory=ThisService.WorkingDirectory;
 p.Start();
 p.WaitForExit();

 Thread.Sleep(3500);

 p = Process();
 p.StartInfo.FileName=Path.Combine(ThisService.WorkingDirectory, "ts3server_startscript.sh");
 p.StartInfo.Arguments="stop";
 p.StartInfo.WorkingDirectory=ThisService.WorkingDirectory;
 p.Start();
 p.WaitForExit();
