tc_game_scripts.script_contents:
from System.IO import Directory, Path
from System import Exception

worldFolder = Path.Combine(ThisService.RootDirectory, 'worlds')

if Directory.Exists(worldFolder):
  try:
    Directory.Delete(worldFolder, True)
    Script.WriteToConsole('Server has been wiped')
  except IOError, e:
      raise Exception('Could not wipe data. Error was: %s' % (e))
else:
  Script.WriteToConsole('No files to delete')

from System.IO import File, Path

if File.Exists(Path.Combine(ThisService.RootDirectory, 'BepInEx', 'plugins', 'ValheimPlus.dll')):
  File.Delete(Path.Combine(ThisService.RootDirectory, 'BepInEx', 'plugins', 'ValheimPlus.dll'))
