tc_game_paths.ext_script:
import clr;
import System;

clr.AddReference("TCAdmin.SDK")

from System import Environment;
from System.Diagnostics import Process;
from System.IO import Path, File;
from TCAdmin.SDK.Misc import WebClient, Server, FileSystem;

# Run WolfET.exe
p = Process();
p.StartInfo.FileName=DownloadedFile;
p.StartInfo.Arguments="/s";
p.Start();
p.WaitForExit();

# Download and run ET_Patch_2_60.exe
patchfile = Path.Combine(GameFilesPath, "__ET_Patch_2_60.exe");
webclient = WebClient();
webclient.DownloadFile("http://downloads.tcadmin.net/games/wolfet-update-windows.aspx", patchfile)
p = Process();
p.StartInfo.FileName=patchfile;
p.StartInfo.Arguments="/s";
p.Start();
p.WaitForExit();

File.Delete(DownloadedFile);
File.Delete(patchfile);

installfolder = "";
if Server.Is64Bits() :
 installfolder = Path.Combine(Environment.GetEnvironmentVariable("ProgramFiles(x86)"), "Wolfenstein - Enemy Territory");
else :
 installfolder = Path.Combine(Environment.GetEnvironmentVariable("ProgramFiles"), "Wolfenstein - Enemy Territory");

FileSystem.CopyDirectory(installfolder, FilesFolderFullName);

