tc_game_scripts.script_contents:
import clr;
import System;

clr.AddReference("TCAdmin.SDK");
clr.AddReference("TCAdmin.GameHosting.SDK");
clr.AddReference("TCAdmin.GameHosting.ModuleApi");
clr.AddReference("TCAdmin.TaskScheduler.SDK");

from System import String;
from TCAdmin.SDK.Misc import ObjectXml;
from TCAdmin.GameHosting.SDK.Automation import GameHostingUpdateInstallInfo;
from TCAdmin.GameHosting.ModuleApi import ModuleDef;
from TCAdmin.TaskScheduler.SDK.Objects import TaskStep;

updateinfo = GameHostingUpdateInstallInfo();
GameHostingUpdateInstallInfo.ServiceId.SetValue(updateinfo, ThisService.ServiceId);
GameHostingUpdateInstallInfo.UpdateId.SetValue(updateinfo, 1);

updatestep = TaskStep();
updatestep.TaskId = ThisTaskStep.Arguments.TaskId;
updatestep.ModuleId = ModuleDef.ModuleId;
updatestep.ProcessId = 10;
updatestep.ServerId = ThisService.ServerId;
updatestep.Name = "Installing Dayz Mod Launcher";
updatestep.Arguments = ObjectXml.ObjectToXml(updateinfo);
updatestep.GenerateKey();
updatestep.Save();

